/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useContext } from 'react';
import Link from 'next/link';
import postContext from '../postContext';
import filterContext from '../filterContext';
import { Panel, ArticleText, ArticlePanel, PanelImage } from './styles';

const Spotlight = () => {
  const { spotLight } = useContext(postContext);
  const { singlePost, setSinglePost } = useContext(filterContext);

  return (
    <Panel>
      <PanelImage>
        <Link href="/">
          <a onClick={() => setSinglePost(spotLight)} />
        </Link>
      </PanelImage>

      <ArticlePanel>
        <ArticleText>
          <h1>
            <Link href="/">
              <a>{spotLight.title}</a>
            </Link>
          </h1>
          <p>{spotLight.body}</p>
          <small>
            Escrito por: <b>{spotLight.name}</b>
          </small>
        </ArticleText>
      </ArticlePanel>
    </Panel>
  );
};

export default Spotlight;
