import React, { useContext } from 'react';
import { Container, PostPanel, PanelImage, PostText, PostBody } from './styles';

import FormatedBody from '../styles/styles';

import filterContext from '../filterContext';

const SingleNotice = () => {
  const { singlePost } = useContext(filterContext);

  return (
    <Container>
      <PostPanel>
        <PanelImage />
        <PostText>
          <h1>{singlePost.title}</h1>
        </PostText>
      </PostPanel>
      <FormatedBody>
        <PostBody>
          <img src="../../images/posts/post6.png" alt="Post image" />
          <PostText>
            <h2>{singlePost.title}</h2>
            <p>{singlePost.body}</p>
          </PostText>
        </PostBody>
      </FormatedBody>
    </Container>
  );
};

export default SingleNotice;
