import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  color: white;
`;

export const PostList = styled.ul`
  list-style-type: none;
`;

export const PostItem = styled.li`
  article {
    display: flex;

    img {
      max-width: 40%;
    }
  }
`;
