import styled from 'styled-components';

export const HeaderNav = styled.header`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  padding: 30px 10%;

  li,
  a {
    font-weight: 500;
    font-size: 16px;
    color: #edf0f1;
    text-decoration: none;
  }

  img {
    cursor: pointer;
  }
`;

export const HeaderMenu = styled.ul`
  list-style: none;

  li {
    display: inline-block;
    padding: 0px 20px;

    a {
      transition: all 01s ease 0s;

      &:hover {
        color: black;
      }
    }
  }
`;
