import styled, { keyframes } from 'styled-components';

export const Container = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  color: white;
  margin: 80px 0 0 0;

  h1 {
    margin: 0px 0 30px 0px;
  }

  p {
    margin: 0 0 20px 0;
  }

  input {
    margin: 0 0 20px 0;
  }
`;
