import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
`;

export const NoticesPanel = styled.div`
  background-image: url('https://blog.rocketseat.com.br/content/images/2019/07/Tipos-de-navegac-a-o-no-React-Native.png');
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  top: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 600px;
`;

export const Notices = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  margin: 0 auto;
`;

export const NoticesList = styled.ul`
  list-style-type: none;
`;

export const NoticesFilterList = styled.ul`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  flex-wrap: wrap;
  list-style-type: none;
`;

export const Notice = styled.li`
  margin: 40px 0 20px 0;
  color: white;
  &:first-child {
    margin-top: 60px;
  }

  article {
    display: flex;
    align-items: center;

    img {
      max-width: 40%;
      margin: 0 50px 0 0;
    }
  }
`;
