import styled from 'styled-components';

export const Whrapper = styled.div`
  width: 1170px;
  margin: 0 auto;
`;

export const NavMenu = styled.nav`
  display: flex;
  background: #d82f4b;
  color: #fff;
  padding: 1em;
  border-radius: 25px;

  ul {
    list-style: none;
    padding: 0;
    margin: 0;
    display: flex;
    flex: 3;
  }

  li {
    margin-right: 1em;
    line-height: 30px;

    a {
      padding: 10px;
      text-decoration: none;
      color: #fff;
    }
  }
`;
