import styled from 'styled-components';

export const Panel = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 30px 10%;
  flex-wrap: wrap;

  img {
    max-width: 100%;
  }

  @media screen and (max-width: 1366px) {
    justify-content: center;
    min-height: calc(100vh - 100px);
    padding: 0;
    img {
      z-index: -3;
      width: 100%;
    }
  }
`;

export const ArticleText = styled.article`
  @media screen and (max-width: 1366px) {
    display: flex;
    align-items: center;
    width: 100%;
    flex-direction: column;
    padding: 20px 0 20px 0;
  }
  h1 {
    margin: 1px;
    color: white;
  }
`;

export const PanelImage = styled.div`
  background-image: url('https://blog.rocketseat.com.br/content/images/2019/07/Tipos-de-navegac-a-o-no-React-Native.png');
  width: 100%;
  height: 100%;
  position: absolute;
  z-index: -3;
  top: 0;
  right: 0;
  bottom: 0;
  left: 85px;
  overflow: hidden;
  width: calc(50% - 130px);
  border-radius: 5px;
  background: no-repeat center center;
  background-size: cover;
`;
