import styled from 'styled-components';
import { FaGithubAlt, FaLinkedin } from 'react-icons/fa';

export const Container = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  color: white;
`;

export const AboutPanel = styled.div`
  background-image: url('https://blog.rocketseat.com.br/content/images/2019/07/Tipos-de-navegac-a-o-no-React-Native.png');
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  top: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 600px;
`;

export const About = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  margin: 0 auto;
`;

export const AboutProfile = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
`;

export const AboutSocial = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: row;
  max-width: 600px;
`;

export const AboutText = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
`;

export const AboutWorks = styled.div``;

export const AboutWorksList = styled.ul`
  list-style-type: none;
`;

export const GitIcon = styled(FaGithubAlt)`
  height: 28px;
  width: 28px;
  margin-right: 20px;
  color: white;
`;
export const LinkedinIcon = styled(FaLinkedin)`
  height: 28px;
  width: 28px;
  color: white;
`;
