import styled from 'styled-components';

export const Panel = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 30px 10%;

  img {
    max-width: 100%;
  }
`;

export const ArticleText = styled.div`
  article {
    h1 {
      margin: 1px;
      color: white;
    }
  }
`;
