import React, { useContext, useState } from 'react';
import {
  Container,
  PostList,
  PostItem,
  PostItemText,
  PostArticle,
  PostImageContainer,
} from './styles';
import postContext from '../postContext';
import AsideMenu from '../AsideMenu/index';

handleClick = e => {
  posts = posts.filter(
    (li, idx, self) => self.map(itm => itm + itm).indexOf(li + li) === idx
  );
};

const Posts = () => {
  const { posts } = useContext(postContext);

  return (
    <Container>
      <PostList>
        {posts.map(post => (
          <PostItem key={post.title}>
            <PostArticle>
              <PostImageContainer>
                <img src="../../images/posts/post7.png" alt="Post 2" />
              </PostImageContainer>
              <PostItemText>
                <h2>{post.title}</h2>
                <p>{post.body}</p>
                <small>{post.name}</small>
                <button onClick={handleClick()} />
              </PostItemText>
            </PostArticle>
          </PostItem>
        ))}
      </PostList>
      <AsideMenu />
    </Container>
  );
};

export default Posts;
