import React, { useContext } from 'react';
import Link from 'next/link';
import postContext from '../postContext';
import {
  BigNotice,
  SmallNotices,
  MainNotices,
  SmallNoticesList,
  SmallNoticesListItem,
  BigNoticeText,
} from './styles';

const Notices = () => {
  const { lastPosts, lastPost } = useContext(postContext);
  return (
    <MainNotices>
      <BigNotice>
        <article>
          <img src="../../images/posts/post9.png" alt="Post gigante" />
          <BigNoticeText>
            <h2>
              <Link href="/">
                <a
                  role="button"
                  tabIndex={0}
                  onClick={() => setSinglePost(lastPost)}
                >
                  {lastPost.title}
                </a>
              </Link>
            </h2>
            <p>{lastPost.body}</p>
            <small>
              Escrito por: <b>{lastPost.name}</b>
            </small>
          </BigNoticeText>
        </article>
      </BigNotice>
      <SmallNotices>
        <h1>Últimas notícias</h1>
        <SmallNoticesList>
          {lastPosts.map(post => (
            <SmallNoticesListItem>
              <article>
                <img src="../../images/posts/post14.png" alt="Post 1" />
                <p>{post.title}</p>
              </article>
            </SmallNoticesListItem>
          ))}
        </SmallNoticesList>
      </SmallNotices>
    </MainNotices>
  );
};

export default Notices;
