import styled from 'styled-components';

export const Panel = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 30px 10%;
`;

export const PanelImage = styled.div`
  background-image: url('https://blog.rocketseat.com.br/content/images/2019/07/Tipos-de-navegac-a-o-no-React-Native.png');
  position: absolute;
  z-index: -3;
  top: 0;
  right: 0;
  bottom: 0;
  left: 85px;
  overflow: hidden;
  width: calc(50% - 130px);
  border-radius: 5px;
  background: no-repeat center center;
  background-size: cover;
  height: 100vh;
`;

export const ArticleText = styled.div`
  article {
    h1 {
      color: white;
    }
  }
`;
