import styled from 'styled-components';
import { FaSearch } from 'react-icons/fa';

export const Container = styled.div`
  display: flex;
  justify-content: space-between;
  color: black;

  @media screen and (max-width: 1366px) {
    margin: 0 10px 0 10px;
  }

  @media screen and (max-width: 1366px) {
    flex-direction: column;
  }
`;

export const PostList = styled.ul`
  display: flex;
  flex-direction: column;
  list-style-type: none;
  max-width: 75%;

  @media screen and (max-width: 1366px) {
    max-width: 100%;
    order: 2;
  }
`;

export const PostItem = styled.li`
  height: 300px;
  border-bottom: 1px #e8e5e5 solid;
  padding: 30px 0 30px 0;

  @media screen and (max-width: 1366px) {
    height: auto;
  }

  &:first-child {
    margin-top: 50px;
  }
`;

export const PostArticle = styled.article`
  display: flex;
  flex-direction: row;
  height: 100%;
  flex-wrap: wrap;
`;

export const PostImageContainer = styled.div`
  max-width: 25%;
  height: 100%;
  display: flex;
  align-items: center;

  img {
    max-height: 50%;

    @media screen and (max-width: 1366px) {
      height: 100%;
      width: 100%;
    }
  }

  @media screen and (max-width: 1366px) {
    max-width: 100%;
  }
`;

export const PostItemText = styled.div`
  max-width: 75%;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  margin: 0 0 0 30px;

  @media screen and (max-width: 1366px) {
    margin: 30px 0 0 0;
    height: 50%;
    max-width: 100%;
  }

  p {
    overflow: hidden;
    text-overflow: ellipsis;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 6;

    @media screen and (max-width: 1366px) {
      -webkit-line-clamp: 3;
    }
  }
`;

export const AsideMenu = styled.aside`
  max-width: 18%;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 80px 0 0 0;

  @media screen and (max-width: 1366px) {
    max-width: 100%;
    order: 1;
  }
`;

export const SearchMenu = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  margin: 0 0 50px 0;

  input {
    font-size: 20px;
    font-weight: 500;
    width: 80%;
    height: 20px;
    padding: 6px 9px;
    text-align: center;
    background: transparent;
    border: none;
    border-radius: 0;
    outline: 0;
    -webkit-box-shadow: none;
    box-shadow: none;
    border-bottom: 2px solid grey;
  }
`;

export const SearchIcon = styled(FaSearch)`
  height: 18px;
  width: 18px;
  color: black;
  margin: 0 0 0 15px;
`;

export const Filter = styled.div`
  width: 100%;

  h1 {
    margin: 0 0 15px 0;
  }

  ul {
    list-style: none;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    flex-wrap: wrap;
  }
`;
