import styled, { keyframes } from 'styled-components';

export const Container = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  color: white;
  margin: 80px 0 0 0;

  h1 {
    margin: 0px 0 30px 0px;
  }

  p {
    margin: 0 0 20px 0;
  }

  input {
    margin: 0 0 20px 0;

    font-size: 20px;
    font-weight: 700;
     width: 80%;
    max-width: 500px;
     height: 35px;
     margin: 4vh 0;
     padding: 6px 9px;
     -webkit-transition: all 0.15s ease;
    transition: all 0.15s ease;
     text-align: center;
     border-bottom: 7px solid #e2e5ed;
     background: transparent;


     border: none;
    border-radius: 0;
    outline: 0;
     -webkit-box-shadow: none;
     box-shadow: none;

`;
