import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  color: white;
`;

export const PostList = styled.ul`
  list-style-type: none;
`;

export const PostItem = styled.li`
  margin: 20px 0 20px 0;

  article {
    display: flex;
    align-items: center;
    img {
      max-width: 40%;
    }
  }
`;
