import React, { useContext } from 'react';
import { Container, SearchMenu, SearchIcon, Filter } from './styles';
import postContext from '../postContext';
import filterContext from '../filterContext';

const AsideMenu = () => {
  const { authors } = useContext(postContext);
  const { setFilter, search, setSearch } = useContext(filterContext);

  const handleClick = () => {
    console.log(search);
    setFilter([...search]);
  };

  return (
    <Container>
      <SearchMenu>
        <input type="text" value={search} onChange={setSearch} />
        <SearchIcon onClick={handleClick} />
      </SearchMenu>
      <Filter>
        <h1>Filtros: </h1>
        <ul>
          {authors.map(author => (
            <li>
              <h3 onClick={() => setFilter(author)}>{author}</h3>
            </li>
          ))}
          <li>
            <h3 onClick={() => setFilter('')}>Todos</h3>
          </li>
        </ul>
      </Filter>
    </Container>
  );
};

export default AsideMenu;
