import React, { useContext, useState } from 'react';
import {
  Container,
  PostList,
  PostItem,
  PostItemText,
  PostArticle,
  PostImageContainer,
} from './styles';
import postContext from '../postContext';
import filterContext from '../filterContext';
import AsideMenu from '../AsideMenu/index';

const Posts = () => {
  const { allPosts } = useContext(postContext);
  const { filter } = useContext(filterContext);

  const [posts, setPost] = useState(allPosts);

  const handleClick = () => {
    const teste = allPosts
      .sort((a, b) => b.metadata.publishedAt + a.publishedAt)
      .map(
        data =>
          (data.metadata.publishedAt = JSON.parse(
            JSON.stringify(data.metadata.publishedAt)
          ))
      );

    setPost([...teste]);

    console.log(teste);
  };

  return (
    <Container>
      <PostList>
        {filter === ''
          ? posts.map(post => (
              <PostItem key={post.title}>
                <PostArticle>
                  <PostImageContainer>
                    <img src="../../images/posts/post7.png" alt="Post 2" />
                  </PostImageContainer>
                  <PostItemText>
                    <h2>{post.title}</h2>
                    <p>{post.body}</p>
                    <small>{post.name}</small>
                    <button onClick={handleClick} />
                  </PostItemText>
                </PostArticle>
              </PostItem>
            ))
          : posts
              .filter(obj => obj.name === filter)
              .map(post => (
                <PostItem key={post.title}>
                  <PostArticle>
                    <PostImageContainer>
                      <img src="../../images/posts/post7.png" alt="Post 2" />
                    </PostImageContainer>
                    <PostItemText>
                      <h2>{post.title}</h2>
                      <p>{post.body}</p>
                      <small>{post.name}</small>
                      <button onClick={handleClick} />
                    </PostItemText>
                  </PostArticle>
                </PostItem>
              ))}
      </PostList>
      <AsideMenu />
    </Container>
  );
};

export default Posts;
