import React, { useContext, useState } from 'react';
import {
  Container,
  PostList,
  PostItem,
  PostItemText,
  PostArticle,
  PostImageContainer,
} from './styles';
import postContext from '../postContext';
import filterContext from '../filterContext';
import AsideMenu from '../AsideMenu/index';

const Posts = () => {
  const { allPosts } = useContext(postContext);
  const { filter } = useContext(filterContext);

  const [posts, setPost] = useState(allPosts);

  const handleClick = () => {
    const teste = posts.filter(animal => {
      return animal.name === 'Nicholas Jordan';
    });

    setPost([...teste]);

    console.log(filter);
  };

  return (
    <Container>
      <button onClick={handleClick} />
      <PostList>
        {filter === '' ? (
          posts.map(post => (
            <PostItem key={post.title}>
              <PostArticle>
                <PostImageContainer>
                  <img src="../../images/posts/post7.png" alt="Post 2" />
                </PostImageContainer>
                <PostItemText>
                  <h2>{post.title}</h2>
                  <p>{post.body}</p>
                  <small>{post.name}</small>
                  <button onClick={handleClick} />
                </PostItemText>
              </PostArticle>
            </PostItem>
          ))
        ) : (
          <div>
            <h1>Sem mais issues neste reposit√≥rio </h1>
          </div>
        )}
      </PostList>
      <AsideMenu />
    </Container>
  );
};

export default Posts;
