import styled from 'styled-components';

export const Panel = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 30px 10%;
  flex-wrap: wrap;

  img {
    max-width: 100%;
  }
`;

export const ArticleText = styled.div`
  @media screen and (max-width: 768px) {
    display: flex;
    justify-content: center;
    width: 100%;
  }
  h1 {
    margin: 1px;

    color: white;
  }
`;
