import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  color: white;
  margin: 80px 0 0 0;

  h1 {
    margin: 0px 0 30px 0px;
  }

  p {
    margin: 0 0 20px 0;
  }

  input {
    margin: 0 0 20px 0;
    font-size: 20px;
    font-weight: 700;
    width: 80%;
    max-width: 500px;
    height: 35px;
    padding: 6px 9px;
    text-align: center;
    background: transparent;

    border: none;
    border-radius: 0;
    outline: 0;
    -webkit-box-shadow: none;
    box-shadow: none;
  }
`;
