import React, { useContext } from 'react';
import { Container, SearchMenu, SearchIcon, Filter } from './styles';
import postContext from '../postContext';

const AsideMenu = () => {
  const { authors, filter, setFilter } = useContext(postContext);

  return (
    <Container>
      <SearchMenu>
        <input type="text" />
        <span onClick={() => setFilter('Jane')}>{filter}</span>
        <SearchIcon />
      </SearchMenu>
      <Filter>
        <h1>Filtros: </h1>
        <ul>
          {authors.map(author => (
            <li>
              <h3>{author}</h3>
            </li>
          ))}
        </ul>
      </Filter>
    </Container>
  );
};

export default AsideMenu;
