import React, { useContext } from 'react';
import {
  Container,
  PostList,
  PostItem,
  PostItemText,
  PostArticle,
} from './styles';
import postContext from '../postContext';
import AsideMenu from '../AsideMenu/index';

const Posts = () => {
  const { posts } = useContext(postContext);

  return (
    <Container>
      <PostList>
        {posts.map(post => (
          <PostItem>
            <PostArticle>
              <div>
                <img src="../../images/posts/post7.png" alt="Post 2" />
              </div>
              <PostItemText>
                <h2>{post.title}</h2>
                <p>{post.body}</p>
                <small>{post.name}</small>
              </PostItemText>
            </PostArticle>
          </PostItem>
        ))}
      </PostList>
      <AsideMenu />
    </Container>
  );
};

export default Posts;
