import styled from 'styled-components';
import { FaGithubAlt, FaLinkedin } from 'react-icons/fa';

export const Container = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  margin-top: 30px;
`;

export const AboutPanel = styled.div`
  background-image: url('');
`;

export const About = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  max-width: 1200px;
  margin: 0 auto;
`;

export const AboutProfile = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
`;

export const AboutSocial = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: row;
  max-width: 600px;
`;

export const AboutText = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
`;

export const AboutWorks = styled.div``;

export const AboutWorksList = styled.ul``;

export const GitIcon = styled(FaGithubAlt)`
  height: 28px;
  width: 28px;
  margin-right: 20px;
`;
export const LinkedinIcon = styled(FaLinkedin)`
  height: 28px;
  width: 28px;
`;
