import styled from 'styled-components';

export const MainNotices = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  color: black;
  flex-wrap: wrap;

  border-bottom: 1px solid black;
`;

export const BigNotice = styled.div`
  max-width: 65%;
  margin-bottom: 40px;
  img {
    width: 100%;
    margin: 0 0 15px 0;
  }
  @media screen and (max-width: 1366px) {
    max-width: 100%;
  }
`;

export const BitNoticeText = styled.div`
  @media screen and (max-width: 1366px) {
    margin: 0 10px 0 10px;
  }
`;

export const SmallNotices = styled.div`
  max-width: 30%;
  margin: 0 0 0 30px;

  @media screen and (max-width: 1366px) {
    margin-top: 30px;
    max-width: 100%;
  }
`;

export const SmallNoticesList = styled.ul`
  list-style-type: none;
`;

export const SmallNoticesListItem = styled.li`
  margin: 0 0 30px 0;

  &:first-child {
    margin-top: 40px;
  }

  img {
    padding: 0px;
    width: 70px !important;
    height: 70px !important;
    line-height: 50px;
    border-radius: 50px;
    margin: 0 10px 0 0;
  }

  article {
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
  }
`;
