import styled from 'styled-components';

export const MainNotices = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  color: white;
  flex-wrap: wrap;
`;

export const BigNotice = styled.div`
  img {
    width: 100%;
    margin: 0 0 15px 0;
  }
`;

export const SmallNotices = styled.div`
  text-decoration: none;
  ul {
  }

  li {
    text-decoration: none;

    img {
      width: 10%;
    }
  }
`;
