/* eslint-disable react/prop-types */
import React, { Component } from 'react';
import { loadGetInitialProps } from 'next/dist/next-server/lib/utils';
import axios from 'axios';
import Posts from '../../components/Posts/index';

export default () => Composed =>
  class extends Component {
    static async getInitialProps(ctx) {
      let pageProps = {};

      if (Composed.getInitialProps) {
        pageProps = await loadGetInitialProps(Composed, ctx);
      }
      return { pageProps };
    }

    componentDidMount = async () => {
      const postsResponse = await axios.get(
        'http://www.mocky.io/v2/5be5e3fa2f000082000fc3f8'
      );
      const authorResponse = await axios.get(
        'http://www.mocky.io/v2/5be5e3ae2f00005b000fc3f6'
      );

      const posts = postsResponse.data.map(a =>
        Object.assign(
          a,
          authorResponse.data.find(b => b.id === a.metadata.authorId)
        )
      );

      const posts2 = posts.data;

      return { pageProps: { ...pageProps, posts } };
    };

    render() {
      const { pageProps } = this.props;

      return (
        <>
          <Composed {...pageProps} />
          <Posts {...pageProps} />
        </>
      );
    }
  };
