import React, { useContext } from 'react';
import postContext from '../postContext';
import {
  BigNotice,
  SmallNotices,
  MainNotices,
  SmallNoticesList,
  SmallNoticesListItem,
  BitNoticeText,
} from './styles';

const Notices = () => {
  const { lastPosts, lastPost } = useContext(postContext);
  return (
    <MainNotices>
      <BigNotice>
        <article>
          <img src="../../images/posts/post9.png" alt="Post gigante" />
          <BitNoticeText>{lastPost.title}</BitNoticeText>
        </article>
      </BigNotice>
      <SmallNotices>
        <h1>Últimas notícias</h1>
        <SmallNoticesList>
          {lastPosts.map(post => (
            <SmallNoticesListItem>
              <article>
                <img src="../../images/posts/post14.png" alt="Post 1" />
                <p>{post.body}</p>
              </article>
            </SmallNoticesListItem>
          ))}
        </SmallNoticesList>
      </SmallNotices>
    </MainNotices>
  );
};

export default Notices;
