import styled from 'styled-components';

export const Panel = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 30px 10%;
`;

export const ArticleText = styled.div`
  padding: 0 12% 10vh 10%;
  flex-direction: column;

  article {
    h1 {
      color: white;
    }
  }
`;
