import React, { useContext } from 'react';
import postContext from '../postContext';
import { Panel, ArticleText, PanelImage } from './styles';

const Spotlight = () => {
  const { spotLight } = useContext(postContext);
  return (
    <Panel>
      <PanelImage />
      <ArticleText>
        <article>
          <h1>{spotLight.title}</h1>
          <p>{spotLight.body}</p>
        </article>
      </ArticleText>
    </Panel>
  );
};

export default Spotlight;
