import React, { useContext } from 'react';
import { Container, SearchMenu, SearchIcon, Filter } from './styles';
import postContext from '../postContext';
import filterContext from '../filterContext';

const AsideMenu = () => {
  const { authors } = useContext(postContext);
  const { filter, setFilter, setDateFilter } = useContext(filterContext);

  return (
    <Container>
      <SearchMenu>
        <input type="text" />
        <span onClick={() => setFilter('Nicholas Jordan')}>{filter}</span>
        <SearchIcon />
      </SearchMenu>
      <Filter>
        <h1>Filtros: </h1>
        <ul>
          {authors.map(author => (
            <li>
              <h3 onClick={() => setFilter(author)}>{author}</h3>
            </li>
          ))}
          <li>
            <h3 onClick={() => setFilter(author)}>{author}</h3>
          </li>
        </ul>
      </Filter>
    </Container>
  );
};

export default AsideMenu;
