import React, { useContext } from 'react';
import { Container, PostList, PostItem, PostItemText } from './styles';
import postContext from '../postContext';
import AsideMenu from '../AsideMenu/index';

const Posts = () => {
  const { posts } = useContext(postContext);

  return (
    <Container>
      <PostList>
        {posts.map(post => (
          <PostItem>
            <article>
              <img src="../../images/posts/post7.png" alt="Post 2" />
              <PostItemText>
                <h1>{post.title}</h1>
                <p>{post.body}</p>
              </PostItemText>
            </article>
          </PostItem>
        ))}
      </PostList>
      <AsideMenu />
    </Container>
  );
};

export default Posts;
