import React, { useContext, useState } from 'react';
import {
  Container,
  PostList,
  PostItem,
  PostItemText,
  PostArticle,
  PostImageContainer,
  AsideMenu,
  SearchMenu,
  SearchIcon,
  Filter,
} from './styles';
import postContext from '../postContext';

const Posts = () => {
  const { posts, authors } = useContext(postContext);

  const [postie, setPost] = useState(posts);

  const handleClick = () => {
    const teste = posts.filter(animal => {
      return animal.name === 'Nicholas Jordan';
    });

    const teste2 = JSON.stringify(teste);

    setPost([teste]);

    console.log(teste2);
  };

  return (
    <Container>
      <PostList>
        <button onClick={handleClick} />
        {postie.map(post => (
          <PostItem key={post.title}>
            <PostArticle>
              <PostImageContainer>
                <img src="../../images/posts/post7.png" alt="Post 2" />
              </PostImageContainer>
              <PostItemText>
                <h2>{post.title}</h2>
                <p>{post.body}</p>
                <small>{post.name}</small>
                <button onClick={handleClick} />
              </PostItemText>
            </PostArticle>
          </PostItem>
        ))}
      </PostList>
      <AsideMenu>
        <SearchMenu>
          <input type="text" />
          <SearchIcon />
        </SearchMenu>
        <Filter>
          <h1>Filtros: </h1>
          <ul>
            {authors.map(author => (
              <li>
                <h3>{author}</h3>
              </li>
            ))}
          </ul>
        </Filter>
      </AsideMenu>
    </Container>
  );
};

export default Posts;
